["Nameplate Added"] = "function (self, unitId, unitFrame, envTable, modTable)\n --   print(\"Nameplate added for unit:\", unitId)\n --  print(\"NPC ID:\", unitFrame.namePlateNpcId)\n    \n    -- Ensure modTable.units is not nil\n    if not modTable.units then\n    --    print(\"Error: modTable.units is nil\")\n        return\n    end\n    \n    -- Hide the TIF (Texture Information Frame) initially\n    if unitFrame.TIF then\n        unitFrame.TIF:Hide()\n  --      print(\"TIF hidden initially\")\n    else\n  --      print(\"Error: TIF frame does not exist\")\n        return\n    end\n    \n    -- Determine the texture to use\n    local texture\n    \n    if modTable.config.test then\n        texture = modTable.RandomTexture()\n    --    print(\"Test mode: Using random texture\")\n    else\n        texture = modTable.units[unitFrame.namePlateNpcId]\n    --    print(\"Normal mode: Texture for NPC\", unitFrame.namePlateNpcId, \":\", texture)\n    end\n    \n    -- If texture is nil, exit the function to avoid errors\n    if not texture then\n   --     print(\"Error: No texture found for NPC\", unitFrame.namePlateNpcId)\n        return\n    end\n    \n    -- Set and show the texture\n    unitFrame.TIF.t:SetTexture(texture)\n    unitFrame.TIF:Show()\n  --  print(\"Texture set and TIF shown\")\n --   print(\"TIF visibility:\", unitFrame.TIF:IsVisible())\n  --  print(\"TIF position:\", unitFrame.TIF:GetPoint())\nend",
["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    if not modTable.units then\n        print(\"NPC ID:\", unitFrame.namePlateNpcId)\n        modTable.units = {}\n    end\n    \n    -- Check if the unit is in the units table or if test mode is enabled\n    if modTable.units[unitFrame.namePlateNpcId] or modTable.config.test then\n        if modTable.config.changecolor then\n            Plater.SetNameplateColor(unitFrame, modTable.config.color)\n        end\n        if modTable.config.hidename then\n            unitFrame.healthBar.unitName:Hide()\n        end\n        if modTable.config.hidehp then\n            unitFrame.healthBar.lifePercent:Hide()\n        end\n        if modTable.config.hideauras then\n            unitFrame.BuffFrame:Hide()\n            unitFrame.BuffFrame2:Hide()\n        end\n    end\nend",
["Initialization"] = "function (modTable)\n    -- Ensure modTable.units is a valid table\n    modTable.units = modTable.units or {}\n    \n    -- Initialize the units table\n    modTable.units = {\n        [119052] = C_Spell.GetSpellTexture(23632), -- War Banner\n        [101398] = C_Spell.GetSpellTexture(211522), -- Psyfiend\n        [3527] = C_Spell.GetSpellTexture(5394), -- Healing Stream Totem\n        [5913] =C_Spell.GetSpellTexture(8143), -- Tremor Totem\n        [61245] = C_Spell.GetSpellTexture(192058), -- Capacitor Totem\n        [2630] = C_Spell.GetSpellTexture(2484), -- Earthbind Totem\n        [53006] = C_Spell.GetSpellTexture(98008), -- Spirit Link Totem\n        [59764] = C_Spell.GetSpellTexture(108280), -- Healing Tide Totem\n        [60561] = C_Spell.GetSpellTexture(51485), -- Earthgrab Totem\n        [105427] = C_Spell.GetSpellTexture(204330), -- Skyfury Totem\n        [5925] = C_Spell.GetSpellTexture(204336), -- Grounding Totem\n        [105451] = C_Spell.GetSpellTexture(204331), -- Counterstrike Totem\n        \n        -- [199220] = C_Spell.GetSpellTexture(199220) --test\n        \n    }\n    -- Debug: Print all NPC IDs and their textures\n    --   print(\"All NPC IDs in modTable.units:\")\n    for npcId, texture in pairs(modTable.units) do\n        --   print(npcId, texture)\n    end\n    \n    -- Debug: Test specific spell texture\n    --  local _, _, texture = C_Spell.GetSpellInfo(135940)\n    --  print(\"Texture for spell 135940:\", texture)\n    \n    -- Function to add or update icon\n    modTable.UpdateIcon = function(unitFrame)\n        --  print(\"Updating icon for unit:\", unitFrame.namePlateNpcId)\n        \n        if modTable.units[unitFrame.namePlateNpcId] then\n            if not unitFrame.TIF then\n                local f = CreateFrame(\"Frame\", nil, unitFrame)\n                f:SetSize(20, 20) -- Adjust size as needed\n                f:SetPoint(\"BOTTOM\", unitFrame, \"TOP\", 0, 5)\n                \n                local t = f:CreateTexture(nil, \"OVERLAY\")\n                t:SetAllPoints(f)\n                f.texture = t\n                \n                unitFrame.TIF = f\n                --print(\"Created new TIF frame for unit:\", unitFrame.namePlateNpcId)\n            end\n            \n            unitFrame.TIF.texture:SetTexture(modTable.units[unitFrame.namePlateNpcId])\n            unitFrame.TIF:Show()\n            -- print(\"Icon should be visible for NPC ID:\", unitFrame.namePlateNpcId)\n            \n            -- Debug: Check frame visibility and position\n            -- print(\"TIF frame exists and is visible:\", unitFrame.TIF:IsVisible())\n            --  print(\"TIF frame position:\", unitFrame.TIF:GetPoint())\n        else\n            -- print(\"No icon defined for NPC ID:\", unitFrame.namePlateNpcId)\n            if unitFrame.TIF then\n                unitFrame.TIF:Hide()\n            end\n        end\n    end\n    \n    -- Nameplate Added function\n    modTable.NameplateAdded = function(self, unitId, unitFrame, envTable)\n        -- print(\"Nameplate added for unit:\", unitId, \"NPC ID:\", unitFrame.namePlateNpcId)\n        modTable.UpdateIcon(unitFrame)\n    end\n    \n    -- Nameplate Updated function\n    modTable.NameplateUpdated = function(self, unitId, unitFrame, envTable)\n        --  print(\"Nameplate updated for unit:\", unitId, \"NPC ID:\", unitFrame.namePlateNpcId)\n        modTable.UpdateIcon(unitFrame)\n    end\n    \n    --  print(\"Plater mod initialization complete\")\nend",
["Constructor"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    if not unitFrame.TIF then\n        -- create frame\n        local f = CreateFrame(\"Frame\", nil, unitFrame.healthBar.FrameOverlay)\n        f:SetFrameStrata(\"BACKGROUND\")\n        f:SetWidth(modTable.config.size)\n        f:SetHeight(modTable.config.size)\n        local t = f:CreateTexture(nil, \"BACKGROUND\")\n        t:SetAllPoints(f)\n        f.t = t\n        t:SetTexCoord(.08, .92, .08, .92)\n        f:SetPoint(\"BOTTOM\", unitFrame.healthBar.FrameOverlay, \"TOP\", 0, modTable.config.y)\n        f:Show() \n        unitFrame.TIF = f\n        -- create border\n        unitFrame.TIF.Border = CreateFrame(\n            \"Frame\", \n            nil, \n            f, \n            \"NamePlateFullBorderTemplate\"\n        )\n        unitFrame.TIF.Border:SetVertexColor(0, 0, 0)\n        unitFrame.TIF.Border:SetBorderSizes (0.5, 0.5, 0.5, 0.5)\n        unitFrame.TIF.Border:UpdateSizes()\n    end\n    \nend\n\n\n",
},
